        private void systemConfig_Load(object sender, EventArgs e)
        {
            if(mainPanel.startAutoPrint)
            {
                buttonAutoPrint.Text = "关闭自动打印";
            }
            else
            {
                buttonAutoPrint.Text = "开启自动打印";
            }
            dateTimeStart.Value = mainPanel.autoPrintStarttime;
            dateTimeEnd.Value = mainPanel.autoPrintEndtime;
            numericUpDown1.Value = mainPanel.autoPrintScanSeconds;
            timer2.Start();
        }

        int printTimerCount = 0;
        private void timer2_Tick(object sender, EventArgs e)
        {
            if (mainPanel.startAutoPrint)
            {
                //labelAutoPrint.Text = "已开启";
                if (printTimerCount++ >= mainPanel.autoPrintScanSeconds)
                {
                    printTimerCount = 0;
                    DataTable dt = mainPanel.logininfcontrol.getCarDetectedHasNotPrint(mainPanel.autoPrintStarttime, mainPanel.autoPrintEndtime, "0", "0", "0");
                    DataRow dr = null;
                    if (dt != null)
                    {
                        int lengthnow = richTextBoxPrintMsg.Text.Length;
                        string info = "扫描到共有" + dt.Rows.Count.ToString() + "辆车等待打印";
                        richTextBoxPrintMsg.AppendText(info + "\r\n");
                        richTextBoxPrintMsg.Select(lengthnow, lengthnow + info.Length);
                        richTextBoxPrintMsg.SelectionColor = System.Drawing.Color.Black;
                        foreach (DataRow dR in dt.Rows)
                        {
                            string msg = "";
                            if (printReportByClid(dR["CLID"].ToString(), out msg))
                            {
                                lengthnow = richTextBoxPrintMsg.Text.Length;
                                msg = dR["CLHP"].ToString() + "打印完成";
                                richTextBoxPrintMsg.AppendText(msg + "\r\n");
                                richTextBoxPrintMsg.Select(lengthnow, lengthnow + msg.Length);
                                richTextBoxPrintMsg.SelectionColor = System.Drawing.Color.Lime;

                            }
                            else
                            {
                                lengthnow = richTextBoxPrintMsg.Text.Length;
                                msg = dR["CLHP"].ToString() + "打印失败：" + msg;
                                richTextBoxPrintMsg.AppendText(msg + "\r\n");
                                richTextBoxPrintMsg.Select(lengthnow, lengthnow + msg.Length);
                                richTextBoxPrintMsg.SelectionColor = System.Drawing.Color.Red;
                            }
                        }
                    }
                    else
                    {
                        int lengthnow = richTextBoxPrintMsg.Text.Length;
                        string info = "没有车辆等待打印";
                        richTextBoxPrintMsg.AppendText(info + "\r\n");
                        richTextBoxPrintMsg.Select(lengthnow, lengthnow + info.Length);
                        richTextBoxPrintMsg.SelectionColor = System.Drawing.Color.Black;
                    }
                }
            }
        }

        private void buttonAutoPrint_Click(object sender, EventArgs e)
        {
            if(buttonAutoPrint.Text=="开启自动打印")
            {
                buttonAutoPrint.Text = "关闭自动打印";
                mainPanel.autoPrintStarttime = dateTimeStart.Value;
                mainPanel.autoPrintEndtime = dateTimeEnd.Value;
                mainPanel.autoPrintScanSeconds = (int)numericUpDown1.Value;
                mainPanel.startAutoPrint = true;
            }
            else
            {
                buttonAutoPrint.Text = "开启自动打印";
                mainPanel.startAutoPrint = false;
            }
        }

        private List<Stream> m_streams;
        //用来记录当前打印到第几页了 
        private int m_currentPageIndex;
        //声明一个Stream对象的列表用来保存报表的输出数据 
        //LocalReport对象的Render方法会将报表按页输出为多个Stream对象。 

        //用来提供Stream对象的函数，用于LocalReport对象的Render方法的第三个参数。
        private Stream CreateStream(string name, string fileNameExtension, Encoding encoding, string mimeType, bool willSeek)
        {
            //如果需要将报表输出的数据保存为文件，请使用FileStream对象。  
            //Stream stream = new MemoryStream(); 
            Stream stream = new FileStream(name + "." + fileNameExtension, FileMode.Create);
            m_streams.Add(stream);
            return stream;
        }


        private void Print()
        {
            m_currentPageIndex = 0;
            if (m_streams == null || m_streams.Count == 0)
                return;
            //声明PrintDocument对象用于数据的打印 
            PrintDocument printDoc = new PrintDocument();
            //printDoc.OriginAtMargins = false;
            //指定需要使用的打印机的名称，使用空字符串""来指定默认打印机 
            printDoc.PrinterSettings.PrinterName = mainPanel.printerName;
            //判断指定的打印机是否可用  
            if (!printDoc.PrinterSettings.IsValid)
            {
                MessageBox.Show("Can't find printer");
                return;
            }
            //声明PrintDocument对象的PrintPage事件，具体的打印操作需要在这个事件中处理。
            printDoc.PrintPage += new PrintPageEventHandler(PrintPage);
            printDoc.PrinterSettings.Copies = 1;//设置打印份数
            //执行打印操作，Print方法将触发PrintPage事件。 
            printDoc.Print();
            //printDoc.Print(); 
        }

        private void PrintPage(object sender, PrintPageEventArgs ev)
        {
            //Metafile对象用来保存EMF或WMF格式的图形， 
            //我们在前面将报表的内容输出为EMF图形格式的数据流。
            m_streams[m_currentPageIndex].Position = 0;
            Metafile pageImage = new Metafile(m_streams[m_currentPageIndex]);
            //指定是否横向打印  
            ev.PageSettings.Landscape = false;
            //这里的Graphics对象实际指向了打印机  
            ev.Graphics.DrawImage(pageImage, ev.PageBounds);
            m_streams[m_currentPageIndex].Close();
            m_currentPageIndex++;
            //设置是否需要继续打印  
            ev.HasMorePages = (m_currentPageIndex < m_streams.Count);
        }

