        public void ref_WaitCar(string lx)
        {
            try
            {
                dt_wait.Rows.Clear();
                    dataGrid_waitcar.Columns["车主"].Visible = checkBoxCZ.Checked;
                    dataGrid_waitcar.Columns["联系电话"].Visible = checkBoxCZ.Checked;
                if (checkBoxExhaust.Checked)
                {
                    DataTable dt = mainPanel.logininfcontrol.getAllCarDetected(lx);
                    DataRow dr = null;
                    if (dt != null)
                    {
                        foreach (DataRow dR in dt.Rows)
                        {
                            if (!mainPanel.isNetUsed && mainPanel.netMode == mainPanel.ZJNETMODE)
                            {
                                if (dR["TEST"].ToString() == "Y")
                                    continue;
                            }
                            if (dR["SFJF"].ToString() == "Y" && checkBoxNoPrinted.Checked == true)
                                continue;
                            dr = dt_wait.NewRow();
                            dr["项目"] = "环保检查";
                            dr["检测编号"] = dR["CLID"].ToString();
                            dr["车牌号"] = dR["CLHP"].ToString();
                            dr["车主"] = dR["CZ"].ToString();
                            dr["联系电话"] = dR["LXDH"].ToString();
                            switch (dR["JCFF"].ToString())
                            {
                                case "ASM": dr["检测方法"] = "稳态工况"; break;
                                case "VMAS": dr["检测方法"] = "瞬态工况"; break;
                                case "JZJS": dr["检测方法"] = "加载减速"; break;
                                case "ZYJS": dr["检测方法"] = "自由加速"; break;
                                case "SDS": dr["检测方法"] = "双怠速"; break;
                                case "SDSM": dr["检测方法"] = "双怠速(摩)"; break;
                                case "LZ": dr["检测方法"] = "滤纸"; break;
                                default: break;
                            }
                            dr["检测时间"] = dR["JCSJ"].ToString();
                            dr["检测结果"] = dR["JCJG"].ToString();
                            dr["费用"] = dR["JCFY"].ToString();
                            switch (dR["SFJF"].ToString())
                            {
                                case "Y":
                                    dr["状态"] = "已领取";
                                    break;
                                case "N":
                                    dr["状态"] = "未领取";
                                    break;
                                default:
                                    dr["状态"] = "未领取";
                                    break;
                            }
                            dt_wait.Rows.Add(dr);
                        }
                    }
                }
                if (checkBoxSafeFinished.Checked)
                {
                    DataTable dt = mainPanel.logininfcontrol.getAllCarSafeDetected(lx);
                    DataRow dr = null;
                    if (dt != null)
                    {
                        foreach (DataRow dR in dt.Rows)
                        {
                            if (dR["SFLQ"].ToString() == "Y" && checkBoxNoPrinted.Checked == true)
                                continue;
                            dr = dt_wait.NewRow();
                            dr["项目"] = "安检完成";
                            dr["检测编号"] = dR["CLID"].ToString();
                            dr["车牌号"] = dR["CLHP"].ToString();
                            dr["车主"] = "--";
                            dr["联系电话"] = "----";
                            dr["检测方法"] = "----";
                            dr["检测时间"] = dR["JCSJ"].ToString();
                            dr["检测结果"] = dR["JCJG"].ToString();
                            dr["费用"] = dR["JCFY"].ToString();
                            switch (dR["SFLQ"].ToString())
                            {
                                case "Y":
                                    dr["状态"] = "已领取";
                                    break;
                                case "N":
                                    dr["状态"] = "未领取";
                                    break;
                                default:
                                    dr["状态"] = "未领取";
                                    break;
                            }
                            dt_wait.Rows.Add(dr);
                        }
                    }
                }
                if (checkBoxSafeUnFinished.Checked)
                {
                    DataTable dt = mainPanel.logininfcontrol.getallCarSafeAtWait();
                    DataRow dr = null;
                    if (dt != null)
                    {
                        foreach (DataRow dR in dt.Rows)
                        {
                            if (dR["SFLQ"].ToString() == "Y" && checkBoxNoPrinted.Checked == true)
                                continue;
                            dr = dt_wait.NewRow();
                            dr["项目"] = "安检待检";
                            dr["检测编号"] = dR["CLID"].ToString();
                            dr["车牌号"] = dR["CLHP"].ToString();
                            dr["车主"] = "--";
                            dr["联系电话"] = "----";
                            dr["检测方法"] = "----";
                            dr["检测时间"] ="--";
                            dr["检测结果"] = dR["JCJG"].ToString();
                            dr["费用"] = dR["JCFY"].ToString();
                            switch (dR["SFLQ"].ToString())
                            {
                                case "Y":
                                    dr["状态"] = "已领取";
                                    break;
                                case "N":
                                    dr["状态"] = "未领取";
                                    break;
                                default:
                                    dr["状态"] = "未领取";
                                    break;
                            }
                            dt_wait.Rows.Add(dr);
                        }
                    }
                }
                ref_zt = false;
                dataGrid_waitcar.DataSource = dt_wait;
                dataGrid_waitcar.FirstDisplayedScrollingRowIndex = Carwait_Scroll;
                dataGrid_waitcar.Sort(dataGrid_waitcar.Columns["检测时间"], ListSortDirection.Descending);
                ref_zt = true;
            }
            catch (Exception)
            {

            }
        }
