        private void button1_Click(object sender, EventArgs e)
        {
            if (selectID[0] != ""&&dataGrid_waitcar.SelectedRows[0].Cells["项目"].Value.ToString()=="环保检查")
            {
                carbg = mainPanel.logininfcontrol.getCarbjbycarID(selectID[0]);
                if (mainPanel.isNetUsed && mainPanel.netMode == mainPanel.ZJNETMODE)
                {
                    try
                    {
                        string resultno;
                        string resultdesc;
                        string testno;
                        JHWebClient.checkResultModel checkmodel = new JHWebClient.checkResultModel();
                        JHinterface jhinterface = new JHinterface();
                        jhinterface.CheckPrintIsEffective(mainPanel.zjurl, selectID[0], out testno, out resultno, out resultdesc, out checkmodel);
                        if (int.Parse(resultno) > 0)
                        {
                            ZJNETLSH = testno;
                            ini.INIIO.saveLogInf("金华联网信息：收到平台验证通过,允许打印,但结果有不一致");
                        }
                        else if (int.Parse(resultno) == 0)
                        {
                            ZJNETLSH = testno;
                            ini.INIIO.saveLogInf("金华联网信息：收到平台验证通过,允许打印");
                        }
                        else
                        {
                            MessageBox.Show("验证未通过:" + resultdesc + "\r\n该车不能查看报告");
                            return;
                        }
                    }
                    catch (Exception er)
                    {
                        MessageBox.Show("发送验证失败:" + er.Message + "\r\n该车不能查看报告");
                        return;
                    }
                }
                else
                {
                    ZJNETLSH = carbg.JYLSH;
                }
                if (carbg.CLID == "-2")
                {
                    MessageBox.Show("查看报告失败", "系统错误!");
                }
                else if (mainPanel.reportStyle == "沈阳")
                {
                    switch (carbg.JCFF)
                    {
                        case "ASM":
                            reportViewSYASM childreportasm = new reportViewSYASM();
