        public static string ZJNETLSH = "";

        private void printReportByClid(string clid)
        {
            if (clid != "")
            {

                carbg = mainPanel.logininfcontrol.getCarbjbycarID(clid);
                SYS.Model.CARINF carbgo = mainPanel.logininfcontrol.getCarInfbyPlate(carbg.CLHP);
                SYS_MODEL.equipmentModel emodel = mainPanel.stationcontrol.getLineEquipInf(mainPanel.stationid, carbg.LINEID);
                if (mainPanel.isNetUsed && mainPanel.netMode == mainPanel.ZJNETMODE)
                {
                    try
                    {
                        string resultno;
                        string resultdesc;
                        string testno;
                        JHWebClient.checkResultModel checkmodel = new JHWebClient.checkResultModel();
                        JHinterface jhinterface = new JHinterface();
                        jhinterface.CheckPrintIsEffective(mainPanel.zjurl, clid, out testno, out resultno, out resultdesc, out checkmodel);
                        if (int.Parse(resultno) > 0)
                        {
                            carbg.LSH = testno;
                            ini.INIIO.saveLogInf("金华联网信息：收到平台验证通过,允许打印,但结果有不一致");
                        }
                        else if (int.Parse(resultno) == 0)
                        {
                            carbg.LSH = testno;
                            ini.INIIO.saveLogInf("金华联网信息：收到平台验证通过,允许打印");
                        }
                        else
                        {
                            MessageBox.Show("验证未通过:" + resultdesc + "\r\n该车不能打印报告");
                            return;
                        }
                    }
                    catch (Exception er)
                    {
                        MessageBox.Show("发送验证失败:" + er.Message + "\r\n该车不能打印报告");
                        return;
                    }
                }
