        private void buttonFsr_Click(object sender, EventArgs e)
        {
            if (selectID[0] != "" && dataGrid_waitcar.SelectedRows[0].Cells["项目"].Value.ToString() == "环保检查")
            {
                string id = selectID[0];
                string clhp = dataGrid_waitcar.SelectedRows[0].Cells["车牌号"].Value.ToString();
                string jcff = dataGrid_waitcar.SelectedRows[0].Cells["检测方法"].Value.ToString();
                StringBuilder temp = new StringBuilder();
                temp.Length = 2048;
                ini.INIIO.GetPrivateProfileString("数据", "服务器", "", temp, 2048, @".\appConfig.ini");
                string ServerName = temp.ToString().Trim();
                ini.INIIO.GetPrivateProfileString("数据", "数据库", "", temp, 2048, @".\appConfig.ini");
                string DBName = temp.ToString().Trim();
                ini.INIIO.GetPrivateProfileString("数据", "用户名", "", temp, 2048, @".\appConfig.ini");
                string UserName = temp.ToString().Trim();
                ini.INIIO.GetPrivateProfileString("数据", "密码", "", temp, 2048, @".\appConfig.ini");
                string Password = temp.ToString().Trim();
                if (ServerName == "" || DBName == "" || UserName == "" || Password == "")
                    throw new Exception("数据库服务器配置不正确或未配置");
                string conStr = "Data Source=" + ServerName + ";Initial Catalog=" + DBName + ";Persist Security Info=True;User ID=" + UserName + ";password=" + Password;
                try
                {
                    DataSet dataReport = new DataSet();
                    SqlConnection con = new SqlConnection(conStr);
                    con.Open();
                    SqlCommand sqlcmd = new SqlCommand();
                    sqlcmd.Connection = con;
                    sqlcmd.CommandText = "select top 1 * from stationNormalInf";
                    SqlDataAdapter sda = new SqlDataAdapter(sqlcmd);
                    DataSet data = new DataSet();
                    sda.Fill(data);
                    con.Close();
                    sda.Dispose();
                    data.Tables[0].TableName="stationNormalInf";
                    dataReport.Tables.Add(data.Tables[0].Copy());

                    sqlcmd.CommandText = "select top 1 * from [已检车辆信息] where clid='"+id+"'";
                    sda = new SqlDataAdapter(sqlcmd);
                    data = new DataSet();
                    sda.Fill(data);
                    con.Close();
                    sda.Dispose();

                    data.Tables[0].TableName = "已检车辆信息";
                    dataReport.Tables.Add(data.Tables[0].Copy());

                    sqlcmd.CommandText = "select top 1 * from [车辆信息] where CLHP='" + clhp + "'";
                    sda = new SqlDataAdapter(sqlcmd);
                    data = new DataSet();
                    sda.Fill(data);
                    con.Close();
                    sda.Dispose();

                    data.Tables[0].TableName = "车辆信息";
                    dataReport.Tables.Add(data.Tables[0].Copy());

                    sqlcmd.CommandText = "select top 1 * from VMAS where clid='" + id + "'";
                    sda = new SqlDataAdapter(sqlcmd);
                    data = new DataSet();
                    sda.Fill(data);
                    con.Close();
                    sda.Dispose();

                    data.Tables[0].TableName = "VMAS";
                    dataReport.Tables.Add(data.Tables[0].Copy());

                    sqlcmd.CommandText = "select top 1 * from SDS where clid='" + id + "'";
                    sda = new SqlDataAdapter(sqlcmd);
                    data = new DataSet();
                    sda.Fill(data);
                    con.Close();
                    sda.Dispose();

                    data.Tables[0].TableName = "SDS";
                    dataReport.Tables.Add(data.Tables[0].Copy());

                    sqlcmd.CommandText = "select top 1 * from Zyjs_Btg where clid='" + id + "'";
                    sda = new SqlDataAdapter(sqlcmd);
                    data = new DataSet();
                    sda.Fill(data);
                    con.Close();
                    sda.Dispose();

                    data.Tables[0].TableName = "Zyjs_Btg";
                    dataReport.Tables.Add(data.Tables[0].Copy());
                    FastReport.Report report = new FastReport.Report();
                    string filename = "";
                    switch (jcff)
                    {
                        case "瞬态工况":
                            filename = @"./fastReport/vmas.frx";
                            report.Load(filename);
                            report.RegisterData(dataReport);
                            report.GetDataSource("stationNormalInf").Enabled = true;
                            report.GetDataSource("已检车辆信息").Enabled = true;
                            report.GetDataSource("车辆信息").Enabled = true;
                            report.GetDataSource("VMAS").Enabled = true;
                            report.Show();
                            break;
                        case "双怠速":
                            //FastReport.Report report = new FastReport.Report();
                            filename = @"./fastReport/sds.frx";
                            report.Load(filename);
                            report.RegisterData(dataReport);
                            report.GetDataSource("stationNormalInf").Enabled = true;
                            report.GetDataSource("已检车辆信息").Enabled = true;
                            report.GetDataSource("车辆信息").Enabled = true;
                            report.GetDataSource("SDS").Enabled = true;
                            report.Show();
                            break;
                        case "自由加速":
                            //FastReport.Report report = new FastReport.Report();
                            filename = @"./fastReport/zyjs.frx";
                            report.Load(filename);
                            report.RegisterData(dataReport);
                            report.GetDataSource("stationNormalInf").Enabled = true;
                            report.GetDataSource("已检车辆信息").Enabled = true;
                            report.GetDataSource("车辆信息").Enabled = true;
                            report.GetDataSource("Zyjs_Btg").Enabled = true;
                            report.Show();
                            break;

                        default:break;
                    }
                }
                catch (Exception err)
                {
                    MessageBox.Show(err.Message);
                    return;
                }
                
            }
        }

